body.script:
jQuery(document).ready(function() {
	jQuery('.rte .wp-caption a').not('.no-colorbox').colorbox({title: function() {
		var caption = jQuery(this).parent().find('.wp-caption-text').text();
		if (caption) {
			return caption;
		} else {
			return false;
		}
	}});
	jQuery('.rte a.photo').not('.no-colorbox').colorbox();
});
