link.script:
$(document).ready(function() {
	
	//make sure the elements are used
	try{
		if ($('#responsive').length){
			$('.leftnavShell').hide();
			$('.main_body_size').css('width','100%');
			$('.middle_when_no_right_side_bar').css('padding-left','0px');
			$('.middle_when_right_side_bar').css('padding-left','0px');
		}else{
			if ($('#menu').length || $('#sidebar').length){
			
				if ($('#menu').length){
					var navElement = 'menu';
				}else{
					var navElement = 'sidebar';
				}
				var navWidth = $('#' + navElement).outerWidth(true);
				var mainWidth = $('.main_body_size').outerWidth();
				var middleWithRightBar = $('.middle_when_right_side_bar').width();
				var middleWithNoRightBar = $('.middle_when_no_right_side_bar').width();
				
				$('#' + navElement).hide();
				
				$('#sidebar .below_left_nav').hide();
				$('.main_body_size').css('width',mainWidth + navWidth + 'px');
				$('.middle_when_right_side_bar').css('width',middleWithRightBar + navWidth + 'px');
				$('.middle_when_no_right_side_bar').css('width',middleWithNoRightBar + navWidth + 'px');
				
			}
		}
	}
	catch(e){
		//do nothing
	}
});

div.script:
	function googleTranslateElementInit() {
			new google.translate.TranslateElement({
			
			pageLanguage: 'en',
			//layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
			autoDisplay: false
		}, 'google_translate_element');
		$('.goog-te-gadget-simple').css('font-size', '10px');
		$('.goog-te-menu-frame').attr('title', 'Google Translator');
		// begin accessibility compliance, requires jQuery
        $('img.goog-te-gadget-icon').attr('alt','Google Translate');
        $('div#goog-gt-tt div.logo img').attr('alt','translate');
        $('div#goog-gt-tt .original-text').css('text-align','left');
        $('.goog-te-gadget-simple .goog-te-menu-value span').css('color','#000');
		$('.goog-te-combo').attr('title','Select A Language');
		//$("html").attr("lang", pageLanguage);
        // end accessibility compliance
	}
