$(function () {
    $('[data-toggle="tooltip"]').tooltip();

    var $comment = $('.event-comment');
    var val = '';
    for (var i = 0; i < $comment.length; i++) {
        if($comment[i]){
            val = anchorme($($comment[i]).html(), {
                truncate:[15,10],
                attributes:[
                    {
                        name:"target",
                        value:"_blank"
                    }
                ],
            });

            $($comment[i]).html(val);
        }
    }
});

