body.script:
    $(document).ready(function(){
        $(".custom-select").each(function(){
            $(this).wrap("");
            $(this).after("");
        });
        $(".custom-select").change(function(){
            var selectedOption = $(this).find(":selected").text();
            $(this).next(".holder").text(selectedOption);
        }).trigger('change');

        //load images 100px before they come in view
        $(".js-lazy-load-img").unveil(100, function() {
			//Recalculate the scroll nav after imae is loaded
			try {
				$.fn.scrollNav('resetPos');
			} catch (e) {
			}
			try{
				$(".component-mhg").matchHeight({byRow: true});
			} catch (e) {
			}
		}); 
    })

	$(window).scroll(function() {
		var heroVideoOffset = $($('.LimelightEmbeddedPlayer').get(0)).offset();
		if(typeof heroVideoOffset !== "undefined"){
        	var navbaroffset = $('.navbar').offset();
			if((navbaroffset.top-heroVideoOffset.top)>=835)
			{
				LimelightPlayer.doPause();
          	}
		}
    });

    if(typeof $.cookie('cq-authoring-mode') === 'undefined'){
        $.cookie('cq-authoring-mode', 'CLASSIC', { expires: 365, path: '/' });
    }
    if(typeof $.cookie('cq-editor-layer') === 'undefined'){
        $.cookie('cq-editor-layer', 'Edit', { expires: 365, path: '/' });
    }

