body.script:
	$(document).ready(function() {
	
		var activeAd;
	
		$('.gdm-ad').mouseover(function(e){
			activeAd = $(this).attr('id');
		});

		function onMessageReceivedForExpanded(e) {
			if(e.data.cmd != "gdmExpandFrame" || e.origin != "https://www.wibw.com") {
				return;
			}
			
			console.log("Got Click: " + e.origin + " - " + e.data.id + " - " + activeAd + " - " + e.data.params['height']);
			
			var adContainer = $("#" + activeAd + " div:first-child").attr('id');
			var iframeID = $("div[id='" + adContainer + "']").children('iframe').attr('id');
			
			 $("iframe[id='" + iframeID + "']").height(e.data.params['height']);
			 $("iframe[id='" + iframeID + "']").css("height", e.data.params['height'] + "px");
		}
	
		if (window.addEventListener) {
			addEventListener('message', onMessageReceivedForExpanded, false);
		} else {
			if (window.attachEvent) {
				attachEvent('onmessage', onMessageReceivedForExpanded);
			} else {
				window.onmessage = onMessageReceivedForExpanded;
			}
		}
	});

