function shareGDM(Network){
var shareURL;
var URL = encodeURI("https://www.wibw.com/content/news/TPS-working-on-response-to-coronavirus-568918301.html");
var Title = encodeURI("TPS working on response to coronavirus");

	ga('send', 'event', 'Shared', 568918301, Network);
	if(Network == "Facebook")
{shareURL = "https://www.facebook.com/sharer/sharer.php?u="+URL;}
if(Network == "Twitter")
{shareURL = "https://twitter.com/intent/tweet?text="+Title+"&url="+URL;}
if(Network == "LinkedIn")
{shareURL = "https://www.linkedin.com/shareArticle?mini=true&url="+URL+"&title="+Title;}
window.open(shareURL,"_blank","width=650, height=525");
}
function shareGDMEmail(){
	ga('send', 'event', 'Shared', 568918301, 'Email');
	window.location.href = "mailto:?subject=TPS working on response to coronavirus&body=https://www.wibw.com/content/news/TPS-working-on-response-to-coronavirus-568918301.html";
}

		
