hr.script:
function dsCloseNav() {
	document.getElementById("dsNavHome").style.width = "0px";
	document.getElementById("dsNavTopics").style.width = "0px";
	document.getElementById("dsNavHome").style.height = "0px";
	document.getElementById("dsNavTopics").style.height = "0px";
	var element = document.getElementById("body_overlay");
	element.classList.remove("overlayActive");
}
function dsOpenNav() {
	var $navIsOpen = false;
	var $dsNavHomeWidth = document.getElementById("dsNavHome").offsetWidth;
	var $dsNavTopics = document.getElementById("dsNavTopics").offsetWidth;
	var element = document.getElementById("body_overlay");
	element.classList.add("overlayActive");
	if(($dsNavHomeWidth != 0)  || ($dsNavTopics != 0)){
		$navIsOpen = true;
	}
	if($navIsOpen == true){
		dsCloseNav();
	}
	else{
	document.getElementById("dsNavHome").style.width = "320px";
	document.getElementById("dsNavHome").style.height = "100%";
	document.getElementById("dsNavTopics").style.width = "0px";	
	document.getElementById("dsNavTopics").style.height = "0px";
	event.preventDefault();
	ga('send', 'event', 'User Action', 'Navigation', 'Open', {'transport': 'beacon'});
	}
}
function opendsNavTopics() {
	document.getElementById("dsNavTopics").style.width = "320px";
	document.getElementById("dsNavTopics").style.height = "100%";
	document.getElementyId("dsNavHome").style.width = "0px";
	document.getElementById("dsNavHome").style.height = "0px";
}
function backdsNavTopics() {
	document.getElementById("dsNavTopics").style.width = "0px";
	document.getElementById("dsNavTopics").style.height = "0px";
	document.getElementyId("dsNavHome").style.width = "320px";
	document.getElementById("dsNavHome").style.height = "100%";
}

function beaconSend(url,eventName,targetValue) {
	event.preventDefault();
	var targetLoc;
	if (typeof targetValue === 'undefined'){
	 	targetLoc = "_self";
	}else{
		targetLoc = targetValue;
	}
	var win = window.open(url,targetLoc);
	ga('send', 'event', 'User Action', eventName, url, {'transport': 'beacon','hitCallback': function(){win.focus();}});
}
