var socialBar = $('.social-media').offset().top-100; 
var f1=$('.site').offset().top-100;
var intersectPosition = $('footer').offset().top-200;
//var socialBar = 250;
$(window).scroll(function() {                   
  var currentScroll = $(window).scrollTop();  
  intersectPosition = $('footer').offset().top-200;
  if(currentScroll+socialBar > intersectPosition){
    $('.social-media').removeClass( 'sticky' );
    $('.social-media').addClass( 'not-sticky' );
  }
  else if (currentScroll >= socialBar) {          
    $('.social-media').addClass( 'sticky' );
    $('.social-media').removeClass( 'not-sticky' );
  } else {                                               
    $('.social-media').removeClass( 'sticky' );
    $('.social-media').removeClass( 'not-sticky' );
  }
});
