        function openHistory() {
            var ddp = $('.breadcrumb-ddw');
            var ddpCont = $(ddp).find('#myDropdown');
            $(ddp).addClass('open');
            $(ddpCont).show();
        }

        function closeHistory() {
            var ddp = $('.breadcrumb-ddw');
            var ddpCont = $(ddp).find('#myDropdown');
            $(ddp).removeClass('open');
            $(ddpCont).hide();
        }

        $(document).ready(function () {
            $('.ddw-link').click(openHistory);
            $('.ddw-link i').click(openHistory);
            $('.your-history-close').click(closeHistory);

            window.onclick = function (event) {

                if (!$(event.target).hasClass('ddw-link') &&
                    !$(event.target).hasClass('dropdown-content') &&
                    !$(event.target).hasClass('brow') &&
                    !$(event.target).hasClass('your-history') &&
                    !$(event.target).hasClass('your-history-close') &&
                    !$(event.target).hasClass('ddw-link-icon')) {

                    if ($('.breadcrumb-ddw').hasClass('open')) {
                        closeHistory();
                    }
                }
            };
        });

link.script:
    $(document).ready(function () {

        //bootstrap the modules
        angular.bootstrap($("[id$='OfficeLocator_Module']"), ['rusa-homepage']);
        angular.bootstrap($("[id$='NearByBranchPanel']"), ['rusa.branchpages']);

        $('.gray2').first().addClass('show-me');

        $('a[data-toggle="collapse"]').click(function () {
            $(this).find('span.toggle-icon').toggleClass('glyphicon-chevron-up glyphicon-chevron-down');
        })

        // Select all links with hashes
        $('a[href*="#"]')
          // Remove links that don't actually link to anything
          .not('[href="#"]')
          .not('[href="#0"]')
          .click(function (event) {
              // On-page links
              if (
                location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '')
                &&
                location.hostname == this.hostname
              ) {
                  // Figure out element to scroll to
                  var target = $(this.hash);
                  target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
                  // Does a scroll target exist?
                  if (target.length) {
                      // Only prevent default if animation is actually gonna happen
                      event.preventDefault();
                      $('html, body').animate({
                          scrollTop: target.offset().top
                      }, 1000, function () {
                          // Callback after animation
                          // Must change focus!
                          var $target = $(target);
                          $target.focus();
                          if ($target.is(":focus")) { // Checking if the target was focused
                              return false;
                          } else {
                              $target.attr('tabindex', '-1'); // Adding tabindex for elements not focusable
                              $target.focus(); // Set focus again
                          };
                      });
                  }
              }
          });
    })

    function showAccordion() {
        var viewPortWidth = document.documentElement.clientWidth;
        if (viewPortWidth < 768) {
            $('.collapse').removeClass('show-me');
            $('.hide-me-anchor').removeClass('hide-me');
        }
        else
        {
            $('.collapse').addClass('show-me');
            $('.hide-me-anchor').addClass('hide-me');
        }
    }

    $(window).resize(showAccordion);
    showAccordion();

    $(document).ready(function()
    {
        var themeStyle = "";
        //console.log(selectedThemeId);
        if (typeof (selectedThemeId) !== "undefined") {
            themeStyle = "theme" + selectedThemeId;
            $('.topmenu').addClass(' ' + themeStyle);
            $('header').addClass(' ' + themeStyle);
            $('.job-filter-desc').addClass(' ' + themeStyle);
            $('.mobile-search-bar').addClass(' ' + themeStyle);
            $('.breadcrumb').addClass(' ' + themeStyle);
        }

        $('#subNav .collapse').on('show.bs.collapse', function (e) {
            $('.navbar-sub.collapse').not(this).removeClass('in');
            $('.topmenu').addClass('active');
        });

        $('#subNav .collapse').on('hide.bs.collapse', function (e) {
            $('.topmenu').removeClass('active');
        });

        // select first item color
        $('select').on('change', function () {
            var $this = $(this);

            if (!$this.val() || $this.val() == -1) {
                this.options[this.selectedIndex] = this.options[this.selectedIndex].className;
            }
        });

    });

