     $(document).ready(function () {

         var identity = 'False';

         console.log('identity -> ' + identity);
         if (identity == "False" && navigator.geolocation) {
             console.log('IN..');
             navigator.geolocation.getCurrentPosition(function (position) {
                 var retZip = "";
                 var lat = position.coords.latitude;
                 var lng = position.coords.longitude;
                 var ip = "";

                 console.log('lat :'+lat);

                 var pos = new google.maps.LatLng(position.coords.latitude, position.coords.longitude);
                 new google.maps.Geocoder().geocode({ 'latLng': pos }, function (res, status) {
                     console.log('address :'+res[0].formatted_address);
                     var zip = res[0].formatted_address.match(/,\s\w{2}\s(\d{5})/);
                     retZip = zip[1];
                     console.log('retZip :'+retZip);

                     if (retZip != null) {

                         $.ajax({
                             type: "POST",
                             url: "/Custom/Services/JobSearchHelper.asmx/SetUserIdentity ",
                             cache: false,
                             contentType: "application/json; charset=utf-8",
                             data: "{'ip':'" + ip + "', 'lat':'" + lat + "', 'lng':'" + lng + "', 'zip':'" + retZip + "','session':''}",
                             dataType: "json",
                             success: function (msg) {
                             },
                             error: function (xhr, msg) {
                                 //alert(msg + '\n' + xhr.responseText);
                             }
                         });
                     }
                 });
             });
         };
     });

const observer = lozad('.lozad', {
        loaded: function (el) {
        // Custom implementation on a loaded element
            if (el.hasAttribute('data-background-image')) {
                el.removeAttribute('data-background-image');
            }
    }
});
observer.observe();
