meta.script:
(function() {
    var increase_height = 550;
    var media_matcher = "(max-width: " + increase_height + "px)";
    var fc;
    var html;
    var resize;
    var make_resizer = function() {
            var throttle = function(func, limit) {
              var inThrottle,
                lastFunc,
                lastRan;
              return function() {
                var context = this,
                  args = arguments;
                if (!inThrottle) {
                  func.apply(context, args);
                  lastRan = Date.now ? Date.now() : (new Date()).getTime();
                  inThrottle = true;
                } else {
                  clearTimeout(lastFunc)
                  lastFunc = setTimeout(function() {
                    if ((( Date.now ? Date.now() : (new Date()).getTime()) - lastRan) >= limit) {
                      func.apply(context, args)
                      lastRan =  Date.now ? Date.now() : (new Date()).getTime() 
                    }
                  }, limit - (( Date.now ? Date.now() : (new Date()).getTime()) - lastRan))
                }
              };
            };

            var resize = throttle(function(evt) {
                if (window.matchMedia) {
                    if (window.matchMedia(media_matcher).matches) {
                        fc.setAttribute("rows", "140,*");
                    } else {
                        fc.setAttribute("rows", "70,*");
                    }
                } else {

                }
            });

        return resize;
    };

    var init = function() {
            fc = document.getElementById("fc");
            html = document.documentElement || document.getElementsByTagName("html")[0];

            if (!fc) {
                fc = document.getElementsByTagName("frameset")[0];
            }
    };

    resize = make_resizer();
    resize();

    if (window.addEventListener) {
        window.addEventListener("load", function() {
            init();
            resize();
            window.addEventListener("orientationchange", resize, false);
            window.addEventListener("resize", resize, false);
        }, false);
    } else {
        window.onload = function() {
            init();
            resize();
            window.onorientationchange = resize;
            window.onresize = resize;
        };
    }

})();
