    var identityCookieName = 'AccessToken'
     function generateIdentityCookie(IdentityModule) {
            var identityModule = new IdentityModule();
            var identityPrefix = 'mcv4';
            // core polyfill's promise before this script runs
            mi.loadAsync('https://api.ipify.org?format=json')
            .then(function(response) {
                var json = JSON.parse(response);
                identityModule.GenerateIdentity(identityCookieName, identityPrefix, [json.ip]);
            })
            .catch(function() {
                identityModule.GenerateIdentity(identityCookieName, identityPrefix, [getGuid()]);
            });
    }
    var accessTokenCookie = mi.getCookie(identityCookieName);
    if (!accessTokenCookie) {
        mi.loadAsync('/wps/source/scripts/libs/identityModulev3.min.js').then(function(response) {
            var temp = {}
            // prevent identiyModule from being added to the window.
            new Function(response).call(temp);
            if (document.readyState === 'complete') {
                generateIdentityCookie(temp.IdentityModule)
            } else {
                window.addEventListener('load', function() {
                    generateIdentityCookie(temp.IdentityModule)
                });
            }
        })
    }
})();

