meta.script:
const newsletterSuccess = (form) => {
    const $div = document.createElement('div');

    $div.classList.add('success');
    $div.innerHTML = "";
    document.querySelector('.grecaptcha-badge').remove();

    if(form.classList.contains('impact')){
        const $email = document.querySelector('.form-control');
        const $button = form.querySelector('.more-link a');
        $email.parentNode.insertBefore($div, $email);
        form.querySelector('p').remove();
        $button.classList.add('success', 'disabled');
        $email.remove();
        $button.addEventListener('click', (e)=> {
            e.preventDefault();
            return false;
        });
    } else {
        form.innerHTML = '';
        form.appendChild($div);
    }
};
function callbackCaptcha(token){
    const $newsletterHomepage = document.querySelector('#newsletter .newsletter-cta-macro');
    const $newsletterSection = document.querySelector('#newsletter-signUpWidget');

    if($newsletterHomepage) {
        onSubmit($newsletterHomepage, token);
    }
    if($newsletterSection) {
        onSubmit($newsletterSection, token);
    }
}
function onSubmit(form, token){
    const data = JSON.parse(form.getAttribute('data-json'));

    data.captchaToken = token;
    form.removeAttribute('data-json');
    $.ajax({
        method: "POST",
        dataType: 'json',
        url: "",
        data: data
    }).done(function (msg) {
        newsletterSuccess(form);
    }).fail(function (jqXHR, textStatus, errorThrown) {
        newsletterSuccess(form);
    });
}

