function popUp(URL) {
        day = new Date();
        id = day.getTime();
        eval("page" + id + " = window.open(URL, '" + id + "', 'toolbar=0,scrollbars=1,location=0,statusbar=1,menubar=0,resizable=1,width=700,height=600,left = 440,top = 212');");
    }

link.script:
$.each($("nav ul li"), function (index, value) {
    var ul = $(value).find("ul");
    $(ul).attr("data-height", $(ul).outerHeight());
    $(ul).css("height", "0px");
    $(ul).css("padding", "0px 10px");
    var arrow = $(value).find(".dropdown-toggle");
    if (ul.length < 1) {
        $(arrow).remove();
    }
    $(arrow).click(function () {
        if ($(ul).hasClass("Open")) {
            $(ul).removeClass("Open");
            $(arrow).removeClass("Open");
            $(ul).css("height", "0px");
            $(ul).css("padding", "0px 10px");
        }
        else {
            $(ul).addClass("Open");
            $(arrow).addClass("Open");
            $(ul).css("height", $(ul).attr("data-height") + "px");
            $(ul).css("padding", "");
        }
        setTimeout(function () {
            $("nav").attr("data-height", $("nav").outerHeight());
        }, 300);
    });
    setTimeout(function () {
        $(ul).addClass("Ready");
    }, 300);
});
$("nav").attr("data-height", $("nav").outerHeight() + 18);
$("nav").css("padding", "0px 10px");
$("nav").css("height", "0px");
setTimeout(function () {
    $("nav").addClass("Ready");
}, 35);
$(".TopNavDisplayer").click(function () {
    if ($("nav").hasClass("Open")) {
        $("nav").css("height", $("nav").attr("data-height") + "px");
        $("nav").removeClass("Open");
        setTimeout(function () {
            $("nav").css("height", "0px");
        }, 1);
        $("nav").css("padding", "0px 10px");
    }
    else {
        $("nav").addClass("Open");
        $("nav").css("height", $("nav").attr("data-height") + "px");
        $("nav").css("padding", "10px");
        setTimeout(function () {
            if ($("nav").hasClass("Open"))
                $("nav").css("height", "auto");
        }, 500);
    }
});

function SocialMover() {
    if (window.innerWidth < 500)
        $(".TopNavSocial a").appendTo("#navigation");
    else
        $(".TopNavSocial a").appendTo(".TopNavSocial");
}
$(document).ready(function () {
    SocialMover();
});

