form.script:
$sbs_plp_submitEvents_BLOCK_48 = [];
$(function () {
    //$sbs_plp_searchFilters_BLOCK_48 = $('#sbs_plp_searchFilters_BLOCK_48');
    var $sbs_plp_form_BLOCK_48 = $('#sbs_plp_form_BLOCK_48');

    $sbs_plp_form_BLOCK_48.find(':input').not('.sbs_plp_query').bind("keypress", function (event) {
        if (event.keyCode == 13) {
            event.preventDefault();
            return false; // ignore default event
        }
    });
    $sbs_plp_form_BLOCK_48.find(':input').not(':checkbox').bind('change', function () {
        var doSubmit = true;
        for (var eventId = 0; eventId < $sbs_plp_submitEvents_BLOCK_48.length; eventId++) {
            var shouldSubmit = $sbs_plp_submitEvents_BLOCK_48[eventId]();
            if (!shouldSubmit) {
                return false;
            }
        }
        if (doSubmit) {
            $sbs_plp_form_BLOCK_48.trigger('submit');
        }
    });
    $('#sbs_plp_form_BLOCK_48 input[type=submit]').bind('click', function () {
        var doSubmit = true;
        for (var eventId = 0; eventId < $sbs_plp_submitEvents_BLOCK_48.length; eventId++) {
            var shouldSubmit = $sbs_plp_submitEvents_BLOCK_48[eventId]();
            if (!shouldSubmit) {
                return false;
            }
        }
        if (doSubmit) {
            $sbs_plp_form_BLOCK_48.trigger('submit');
        }
    });
    $sbs_plp_form_BLOCK_48.find(':checkbox').bind('click', function () {
        $sbs_plp_form_BLOCK_48.trigger('submit');
    });
});

hr.script:
$(function() {
    $(".ex-content-item").slideUp(1);
});

$(".ex-item-cont h4").css("cursor", "pointer").on("click", function() {
    var $this = $(this).parent();
    var oS;

    if($(".ex-item-cont.opened").length == 0) {
        $this.find(".ex-content-item").stop().slideDown(540, function() {
            $this.addClass("opened");
            oS = $this.offset().top - 65;
            if($(document).scrollTop() > oS) { $("body,html").stop().animate({scrollTop: oS  + "px"}, 500); }
        });
    } else {
        if($this.hasClass("opened")) {
            $this.find(".ex-content-item").stop().slideUp(480, function() {
                $this.removeClass("opened");
            });
        } else {
            $this.find(".ex-content-item").stop().slideDown(510);
            $(".ex-item-cont.opened .ex-content-item").stop().slideUp(350, function() {
                $(this).parent().removeClass("opened");
                $this.addClass("opened");
                oS = $this.offset().top - 65;
                if($(document).scrollTop() > oS) { $("body,html").stop().animate({scrollTop: oS  + "px"}, 500); }
            });
        }

    }

});

