		$(function() {
			$(window).keyup(function (e) {
     		  	  var code = (e.keyCode ? e.keyCode : e.which);
			  if( document.activeElement.classList.contains('nav-2nd') || document.activeElement.classList.contains('nav-3rd') )
			  {	
				$(".focuser").css("color","");
			  }
			  else if( document.activeElement.id != "skip_link") {
			  	console.log("on skip");
				$("#skip_link").css({"left": "-999px", "position": "absolute", "top": "auto", "width": "1px", "height": "1px", "overflow": "hidden", "z-index": "-999", "color":""});	
				$(".focuser").css("color","");
			  	
			  }
			  else {
				$(".focuser").css("color","");
			  	$(".focuser").css("outline-color"," ");
			  }
			  $(".focuser").removeClass("focuser");
        	  	  if (code == 9 && $(':focus').length) {
				$(document.activeElement).addClass("focuser");
				if( $(".focuser").hasClass("goog-te-menu-value") ) {
				}
				if( document.activeElement.classList.contains('nav-2nd') || document.activeElement.classList.contains('nav-3rd') )
				{
					//console.log("in nav");
					$(".focuser").css("color","yellow");
				}
				else if( document.activeElement.id == "skip_link") {
			  		//console.log("on skip");
					$("#skip_link").css({"left": "auto", "position": "absolute", "top": "auto", "width": "auto", "height": "auto", "overflow": "auto", "z-index": "999", "margin-left": "2%", "outline-color": "blue" });
			  	}
				else {
					$(".focuser").css("outline-color","blue");
				}
				//console.log(document.activeElement);
				
        	   	  }
    			});
		});

