body.script:
$(function() {
  //for feedback modal
  $('#feedback-modal').on('loaded.bs.modal', function (e) {
	  //for feedback modal
	  var recaptcha_site_key = $('#feedback-form-recapcha').attr('site-key');
	  var recaptcha_widget_id = grecaptcha.render("feedback-form-recapcha", {"sitekey":recaptcha_site_key});

	  $("#frm-feedback").submit(function() {
			var firstName = $("#frm-feedback input[name=firstName]").val().trim();
			var lastName = $("#frm-feedback input[name=lastName]").val().trim();
			var phone = $("#frm-feedback input[name=phone]").val().trim();
			var email = $("#frm-feedback input[name=email]").val().trim();
			var city = $("#frm-feedback input[name=city]").val().trim();
			var state = $("#frm-feedback select[name=state]").val();
			var subject = $("#frm-feedback input[name=subject]").val().trim();
			var contactMethod = "";
			if ($("#frm-feedback input[name=contactMethod]:checked").length > 0) {
				contactMethod = $("#frm-feedback input[name=contactMethod]:checked").val();
			}
			var comments = $("#frm-feedback textarea[name=comments]").val().trim();

			var g_recaptcha_response = grecaptcha.getResponse(recaptcha_widget_id);

			if (!email.isEmail()) {
				alert("Please enter a valid email.");
				return false;
			}

			if (comments == "") {
				alert("Please enter your comments.");
				return false;
			}

			var formData = {"firstName": firstName, "lastName": lastName, "phone": phone, "email": email,
							"city": city, "state": state, "subject": subject, "contactMethod": contactMethod,
							"comments": comments, "g-recaptcha-response": g_recaptcha_response};

			var actionURL = "/action.cfm?md=form&task=submitSiteFeedback";

			$.ajax({
				url: actionURL,
				type: "post",
				data: formData
			}).done(function(data) {
				if (data == "1") {
					$('#feedback-modal .form-body').hide();
					$('#feedback-modal button[type=submit]').hide();
					$('#feedback-modal .submission-response').show();
				} else if (data == "-1") {
					//Recaptcha.reload();
					grecaptcha.reset(recaptcha_widget_id);
					alert("You did not pass the reCAPTCHA test.");
				} else {
					alert("Sorry, an error ocurred on the server.");
				}
			}).fail(function() {
				alert("Sorry, an error ocurred on the server.");
			});

			return false;
		});
	});
});
