var DelayedScriptExecution_queue = []; 
var DelayedScriptExecution_finished = false;
function DelayedScriptExecution_enqueue(fn) {
    if (DelayedScriptExecution_finished) {
        fn();
    } else {
        DelayedScriptExecution_queue.push(fn);
    }
}

