    $(function () {
        //test if it is mobile view or not and add classes accordingly. Needed because different sites use different breakpoints for mobile
        if ($('.sidenav .mobile_only').is(':visible')) {
            $('.sidenav').addClass('vi-sidenav-mobile');
        } else {
            $('.sidenav').addClass('vi-sidenav-desktop');
        }
        //need to also check for mobile view on resize and change classes if needed
        var sidenavResizeTimer;

        $(window).on('resize', function (e) {

            clearTimeout(sidenavResizeTimer);
            sidenavResizeTimer = setTimeout(function () {

                if ($('.sidenav .mobile_only').is(':visible')) {
                    $('.vi-sidenav-desktop').addClass('vi-sidenav-mobile').removeClass('vi-sidenav-desktop');
                } else {
                    $('.vi-sidenav-mobile').addClass('vi-sidenav-desktop').removeClass('vi-sidenav-mobile');
                }
            }, 250);

        });

        //expand/collapse function
        $("#leftNav_38_0_37 .vi-sidenav-toggle").click(function () {
            var item = $(this),
                parent = item.parent("li");

            if (parent.length) {
                if (parent.hasClass("sidenav_expanded")) {
                    parent.removeClass("sidenav_expanded");
                } else {
                    parent.addClass("sidenav_expanded");
                }
            }

            if (item.html() === "+") {
                item.html('-');
            } else {
                item.html('+');
            }
        });
    });

function shareLink(shareType) {
    var shareUrl = "";
    var shareTitle = document.itemTitle ? encodeURIComponent(document.itemTitle.trim()).replace(/[!'()*]/g, escape) : "Press+Releases+%2b+Latest+News";
    switch (shareType) {
        case "facebook":
            shareUrl = "http://www.facebook.com/sharer.php?u=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f&t=" + shareTitle;
            break;
        case "linkedin":
            shareUrl = "http://www.linkedin.com/shareArticle?mini=true&url=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f&title=" + shareTitle;
            break;
        case "twitter":
            //Below link works in iOS and other OS
            shareUrl = "https://twitter.com/intent/tweet?url=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f&text=Press+Releases+%2b+Latest+News+%7c+";
            break;
        case "delicious":
            shareUrl = "https://secure.delicious.com/login?noui=yes&v=4&jump=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f%26title%3D" + shareTitle + "%26notes%3D%26tags%3D%26v%3D4%26noui%3D%26share%3Dyes%26jump%3Dclose%26time%3D1239993443";
            break;
        case "myspace":
            shareUrl = "http://www.myspace.com/Modules/PostTo/Pages/?u=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f&t=" + shareTitle + "&c=";
            break;
        case "googlebookmark":
            shareUrl = "http://www.google.com/bookmarks/mark?op=add&bkmk=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f&title=" + shareTitle;
            break;
        case "stumbleupon":
            shareUrl = "http://www.stumbleupon.com/submit?url=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f&title=" + shareTitle;
            break;
        case "digg":
            shareUrl = "http://digg.com/submit/?url=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f";
            break;
        case "tumblr":
            shareUrl = "https://www.tumblr.com/share/link?url=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f&name=" + shareTitle;
            break;
        case "reddit":
            shareUrl = "http://www.reddit.com/submit?url=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f&title=" + shareTitle;
            break;
    }
    if (shareUrl.length > 0) window.open(shareUrl, "_blank");
}

    $(function () {
        var widgetContext = "widget_39_0_37";

        $("#" + widgetContext + " #share").click(function () {
            if (!$("#" + widgetContext + " div#share").hasClass("click-active"))
                $("#" + widgetContext + " div#share").find("ul").show();
            else
                $("#" + widgetContext + " div#share").find("ul").hide();
        });

        $("div").click(function () {
            if ($("#" + widgetContext + " div#share").hasClass("click-active")) {
                $("#" + widgetContext + " div#share").find("ul").hide();
            }
        });

        $(document).click(function (e) {
            if (!$(e.target).closest("#share").length > 0) {
                $("#" + widgetContext + " div#share").removeClass("click-active");
                $("#" + widgetContext + " div#share").find("ul").hide();
            }
        });

        var shareContainerTimeout = null;
        $("#" + widgetContext + " #share").bind('mouseover', function () {
            //If not relate to click event
            if (shareContainerTimeout) {
                clearTimeout(shareContainerTimeout);
                shareContainerTimeout = null;
            }
            shareContainerTimeout = setTimeout(function () {
                if (!$("#" + widgetContext + " div#share").hasClass("click-active"))
                    $("#" + widgetContext + " div#share").find("ul").show();
            }, 100);
        });

        $("#" + widgetContext + " #share").bind('mouseleave', function () {            
            //If not relate to click event     
            if (shareContainerTimeout) {
                clearTimeout(shareContainerTimeout);
                shareContainerTimeout = null;
            }
            shareContainerTimeout = setTimeout(function () {
                if (!$("#" + widgetContext + " div#share").hasClass("click-active"))
                    $("#" + widgetContext + " div#share").find("ul").hide();
            }, 200);
        });

        $("header#" + widgetContext + " a.feedback_link").click(function () {
            var windowHeight = 485;
            if (window.innerWidth <= 648) {
                windowHeight = 545;
            }
            var opts = {
                title: "Feedback",
                url: "/Template/GetFeedbackPartial?feedbackUrl=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f",
                useFrame: true,
                height: windowHeight,
                onClosed: function (result) {
                    if (result != undefined && result.IsOk == true) {
                        $.refreshTempMessage(result.Message);
                    }
                },
                skin: 'viClientDialog feedback_lightbox',
                fixed: false
            };
            $.viClientDialog(opts).open();
        });

        //Safari iOS: No click event
        $("header#" + widgetContext + " a.send_share_email").bind("click touchstart", function () {
            var shareEmailTitle = document.itemTitle ? encodeURIComponent(document.itemTitle.trim()).replace(/[!'()*]/g, escape) : "Press+Releases+%2b+Latest+News";
            var opts = {
                title: "Click to submit an email online",
                url: "/Template/GetShareEmailPartial?shareUrl=http%3a%2f%2fwww.lafourchegov.org%2fHome%2fComponents%2fNews%2fNews%2f5851%2f17%3fbacklist%3d%252f" + "&shareTitle=" + shareEmailTitle,
                useFrame: true,
                height: 485,
                onClosed: function (result) {
                    if (result != undefined && result.IsOk == true) {
                        $.refreshTempMessage(result.Message);
                    }
                },
                skin: 'viClientDialog send_share_email_lightbox',
                fixed: false
            };
            $.viClientDialog(opts).open();
        });

        

    });

