body.script:
$(document).observe("dom:loaded", function(){
 handleShareboxToggleEvent = function(e) { 
 e.preventDefault();
 $("sharebox-links").toggle();
 $("sharebox-toggle").toggle();
 };
 //$("sharebox-toggle").observe("click", handleShareboxToggleEvent );
 //$("sharebox-links-close").observe("click", handleShareboxToggleEvent);
 function PopupCenter(url, title, w, h) {
 // Fixes dual-screen position Most browsers Firefox
 var dualScreenLeft = window.screenLeft != undefined ? window.screenLeft : screen.left;
 var dualScreenTop = window.screenTop != undefined ? window.screenTop : screen.top;

 var width = window.innerWidth ? window.innerWidth : document.documentElement.clientWidth ? document.documentElement.clientWidth : screen.width;
 var height = window.innerHeight ? window.innerHeight : document.documentElement.clientHeight ? document.documentElement.clientHeight : screen.height;

 var left = ((width / 2) - (w / 2)) + dualScreenLeft;
 var top = ((height / 2) - (h / 2)) + dualScreenTop;
 var newWindow = window.open(url, title, 'scrollbars=0,personalbar=0,toolbar=0,resizable=0,width=' + w + ', height=' + h + ', top=' + top + ', left=' + left);

 // Puts focus on the newWindow
 if (window.focus) {
 newWindow.focus();
 }
 }
 $$(".sharebox-links .sharebox-popup").each(function(p){
 p.observe("click", function(e){
 e.preventDefault(); 
 width = this.getAttribute("data-popupWidth") || 650;
 height = this.getAttribute("data-popupHeight") || 306; 
 PopupCenter(this.href, '', width, height);
 
 return false;
 }); 
 });

});

 $(document).observe('dom:loaded',function(e){
 var sidebar_content = new tpcg.ui.SidebarLayout( $("sidebarNewsView") );

 // Assign link events:
 var toggle_link = $('toggleSidebarLink-ShowSidebar');
 if ( toggle_link ) toggle_link.observe('click',sidebar_content.toggleHandler.bind(sidebar_content));
 
 
 toggle_link = $('toggleSidebarLink-HideSidebar');
 if ( toggle_link ) toggle_link.observe('click',sidebar_content.toggleHandler.bind(sidebar_content));
 });

 $(document).observe('dom:loaded',function(){
 try{
 var resize_content_list = $$('.FontResize-Content');
 if ( resize_content_list.length == 0 ){
 resize_content_list = $$('.LayoutDefault-Content'); 
 }
 
 if ( resize_content_list.length != 0 ){
 var font_resize = new tpcg.ui.FontResize(resize_content_list); 
 var click_handler = function($super,click_event){ click_event.stop(); $super(); };
 
 font_resize.decrement = font_resize.decrement.wrap(click_handler);
 $('fontSize-Decrease').observe('click',font_resize.decrement.bind(font_resize));
 
 font_resize.increment = font_resize.increment.wrap(click_handler);
 $('fontSize-Increase').observe('click',font_resize.increment.bind(font_resize));
 
 font_resize.reset = font_resize.reset.wrap(click_handler);
 $('fontSize-Reset').observe('click',font_resize.reset.bind(font_resize));
 }

 }catch(e){alert(e.message);} 
 });

$(document).observe('dom:loaded',function(){
$$(".Department-Menu .MenuSection").each(
function(section){
var link = section.select('.MenuSwitch-ExpandLink')[0];
var menu = new tpcg.ui.AccordianMenu( link.id, new tpcg.ui.ToggleSection( section.select('.MenuSection-Content')[0] ), link, link.select('.MenuSwitch-ExpandButton')[0] );
menu.restore();
menu.button.observe('click',menu.onOpenHandler.bind(menu));
link.observe('click',menu.onLoadHandler.bind(menu));
}
);
});

$(document).observe("dom:loaded", function(){
 MediaPlayerXhr = Class.create(tpcg.xhr.Module,{
 
 });
 var sidebarVideoLinks = $$(".SidebarModule .VideoLink-Tpcg");
 var i = 0;
 while ( i < sidebarVideoLinks.length) {
 sidebarVideoLinks[i].observe("click", function(ClickEvent){
 ClickEvent.stop();
 var mod = new tpcg.xhr.Module('media_player', 'index', 
 {
 parameters: "videoId=" + this.getAttribute("data-videoId"),
 process: function(JsonData){
 if(JsonData){ 
 if(JsonData.html){
 $("sidebarVideoContainer").update(JsonData.html);
 
 }
 if(JsonData.js){ 
 eval.call(window,JsonData.js); 
 }

 lightwindow_opts = {
 "loadingAnimation": "true",
 "type": "inline",
 "href": "#sidebarVideoContainer"
 };
 myLightWindow.activateWindow(lightwindow_opts);
 }
 }
 }
 );
 tpcg.xhr.get(mod);
 }.bind(sidebarVideoLinks[i]));
 i++;
 }
});

// trigger the javascript loaded event:
$(document).observe('dom:loaded',function(){
var focus_field = $$('.FormEntry-Error .FocusField')[0];
focus_field = focus_field ? focus_field : $$('.FocusField')[0];
if ( focus_field ){ try{ focus_field.focus(); focus_field.select(); }catch(e){} }
$$('.JsPageInitialize').invoke('removeClassName','JsPageInitialize');
$(document).fire('js:loaded');
});
