                if($('#ui-btn-mypasskey').length) {
                	var pSvg = '';
                    $('
').insertBefore($('#gb-translate'));
                    $('.cs-footer-link.passkeys').append($('#ui-btn-mypasskey'));
                    $('.cs-footer-link.passkeys a').addClass('cs-fc cs-aic gb-mystart-link').text('Passkeys').attr('position', 'bottomright').prepend(pSvg);
                } 

                $('.bullet').html('');

                $('.ui-widget.app.navigation li').each(function(k, v){
