                    case "13":
                      ariaDate = "Thirteenth";
                    break;
                    case "14":
                      ariaDate = "Fourteenth";
                    break;
                    case "15":
                      ariaDate = "Fifteenth";
                    break;
                    case "16":
                      ariaDate = "Sixteenth";
                    break;
                    case "17":
                      ariaDate = "Seventeenth";
                    break;
                    case "18":
                      ariaDate = "Eighteenth";
                    break;
                    case "19":
                      ariaDate = "Nineteenth";
                    break;
                    case "20":
                      ariaDate = "Twentieth";
                    break;
                    case "21":
                      ariaDate = "Twenty-first";
                    break;
                    case "22":
                      ariaDate = "Twenty-second";
                    break;
                    case "23":
                      ariaDate = "Twenty-third";
                    break;
                    case "24":
                      ariaDate = "Twenty-fourth";
                    break;
                    case "25":
                      ariaDate = "Twenty-fifth";
                    break;
                    case "26":
                      ariaDate = "Twenty-sixth";
                    break;
                    case "27":
                      ariaDate = "Twenty-seventh";
                    break;
                    case "28":
                      ariaDate = "Twenty-eighth";
                    break;
                    case "29":
                      ariaDate = "Twenty-ninth";
                    break;
                    case "30":
                      ariaDate = "Thirtieth";
                    break;
                    case "31":
                      ariaDate = "Thirty-first";
                    break;
                    default: ariaDate = eventNumberDay;
                  }
                  $('.joel-day').remove();
                  $(this).find('.sw-calendar-block-date').attr('aria-label', ariaMonth+" "+ariaDate);


                })



                $.each($('.upcomingevents li .sw-calendar-block-time'), function(){
                        $(this).prependTo($(this).parent());

                });

		/****** REMOVE First 0S *******/
		$.each($('.jeremy-date'), function(){
		        var text = $(this).text(),
		            html = '';
		        if(text.slice(0, 1) == 0){
		            html += text.slice(1);
		        } else {
		            html += text.slice(0);
		        }
		        $(this).text(html);
		});// End each
	}

