                    //--> Keyboard navigation
                    $('.cs-school-btn').keydown(function(e){

                        var c = e.which,
                            panel = $(this).attr('aria-controls'),
                            link = $('#' + panel + ' a[data-link="1"][data-column="1"]');

                        if(c == KeyCodes.right || c == KeyCodes.down || c == KeyCodes.left || c == KeyCodes.up || c == KeyCodes.enter || c == KeyCodes.space || c == KeyCodes.esc) {

                            e.preventDefault();

                            if(c == KeyCodes.right) {
                                $(this).next().focus();
                            } else if (c == KeyCodes.down || c == KeyCodes.enter || c == KeyCodes.space) {
                                $(this).click();
                                link.delay(200).focus();
                            } else if(c == KeyCodes.left) {
                                $(this).prev().focus();
                            } else if(c == KeyCodes.up) {
                                $('#gb-school-btn').focus();
                            } else if(c == KeyCodes.esc) {
                                _this.CloseSchoolMenu();
                            }

                        }

                    });

                    //--> Link navigation
                    $('.cs-school-wrapper a').keydown(function(e){

                        var k = e.which,
                            KeyCodes = _this.KeyCodes,
                            l = parseInt($(this).attr('data-link')),
                            c = parseInt($(this).attr('data-column'));

                        if(k == KeyCodes.down || k == KeyCodes.up || k == KeyCodes.left ||	k == KeyCodes.right || k == KeyCodes.esc ) {

                            e.preventDefault();

                            if(k == KeyCodes.down && $('.cs-school-wrapper.active a[data-link="' + (l + 1) + '"][data-column="' + c + '"]').length == 0) {
                                $('.cs-school-wrapper.active a[data-link="' + 1 + '"][data-column="' + c + '"]').focus();
                            } else if(k == KeyCodes.down) {
                                $('.cs-school-wrapper.active a[data-link="' + (l + 1) + '"][data-column="' + c + '"]').focus();
                            } else if(k == KeyCodes.up && l == 1) {
                                $('.cs-school-btn.active').focus();
                            } else if(k == KeyCodes.up) {
                                $('.cs-school-wrapper.active a[data-link="' + (l - 1) + '"][data-column="' + c + '"]').focus();
                            } else if(k == KeyCodes.right && $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + (c + 1) + '"]').length == 0) {
                                $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + 1 + '"]').focus();
                            } else if(k == KeyCodes.right) {
                                $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + (c + 1) + '"]').focus();
                            } else if(k == KeyCodes.left && $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + (c - 1) + '"]').length == 0) {
                                $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + 4 + '"]').focus();
                            } else if(k == KeyCodes.left) {
                                $('.cs-school-wrapper.active a[data-link="' + l + '"][data-column="' + (c - 1) + '"]').focus();
                            } else if(k == KeyCodes.esc) {
                                _this.CloseSchoolMenu();
                            }

                        }

                    });

                });


                //--> Mega School List Functionality
                $('#gb-school-btn').click(function(){

					var pageH = $('#gb-page').height();

                    if($(this).hasClass('open')) {

                        $('nav, main, footer, .gb-header.left, #gb-translate, #gb-single-icon').attr('aria-hidden', 'false');
                        $('#gb-page').removeClass('cs-modal-open');
                        $(this).removeClass('open');
                        $(this).attr('aria-label', 'Open School List');
                        $('#cs-mega-schoollist-wrapper').attr('aria-hidden', 'true').fadeOut();

                    } else {

                        $('nav, main, footer, .gb-header.left, #gb-translate, #hp-content-upper, #hp-content-lower, #sp-content').attr('aria-hidden', 'true');
                        $('#gb-page').addClass('cs-modal-open');
                        $(this).addClass('open');
                        $(this).attr('aria-label', 'Close School List');
                        $('#cs-mega-schoollist-wrapper')
                        	.attr('aria-hidden', 'false')
                            .fadeIn(function(){

                            	$('button.cs-school-btn.active').focus();

                            });


                        $('#cs-closemodal-btn').unbind('click').click(function(){

                            _this.CloseSchoolMenu();

                        });

                    }

                }).keydown(function(e){

                	var c = e.which;

                    if((c == KeyCodes.tab && $(this).hasClass('open')) || (c == KeyCodes.down && $(this).hasClass('open'))) {

                    	e.preventDefault();

                        $('button.cs-school-btn.active').focus();

                    }

                });

            }

            $('#gb-logo-icon a').focus(function(){
            	if($('#gb-page').hasClass('cs-modal-open')) {
            		$('#cs-closemodal-btn').focus();
                }
            });

        	$('#skipLink').unbind('focus').focus(function(){
            	if($('#gb-page').hasClass('cs-modal-open')) {
            		$('#cs-closemodal-btn').focus();
                } else {
                	$('#gb-page').addClass('skip-content');
                }
            }).blur(function(){
            	$('#gb-page').removeClass('skip-content');
            });

        },

        "CloseSchoolMenu": function() {
        	$('nav, main, footer, .gb-header.left, #gb-translate, #hp-content-upper, #hp-content-lower, #sp-content').attr('aria-hidden', 'false');
            $('#gb-page').removeClass('cs-modal-open');
            $('#gb-school-btn').removeClass('open');
            $('#gb-school-btn').attr('aria-label', 'Open School List');
            $('#cs-mega-schoollist-wrapper').attr('aria-hidden', 'true').fadeOut();
            $('#gb-school-btn').focus();
        },

