var DFP 	= { 
	'banners': 		[],
	'device': 		function(){
		return (typeof device !== 'undefined')? device: 'desktop';
	},
	'load': 		function(position){

		// make sure we're only loading the proper device's banners
		if (DFP.banners[position]['device'] == DFP.device()){

			// mark position as loaded
			DFP.banners[position]['loaded']	= true;

			// load ad
			googletag.cmd.push(function() {
				googletag.pubads().refresh([DFP.banners[position]['slot']]);
			});

			console.log('>> dfp loaded:',position);

			// mark in GA
			if (typeof _event == 'function'){
				_event('dfp-adunit','viewport',position);
			}	

		}

	},
	'viewport': 	function(){

		// iterate adds almost in viewport
		$('.dfp-adunit:in-viewport').each(function(){

			// grab the ad unit's position
			var position 	= $(this).attr('id');

			// make sure this ad unit hasn't already been loaded
			// also confirm we're loading the proper device's banners
			if (DFP.banners[position]['loaded'] == false && DFP.banners[position]['device'] == DFP.device())
			{
				DFP.load(position);
			}

		});
	}
};

