	markup: 		function(){
		var markup 		= '';
		var firstfew 	= stack_read_more.firstfew();
		var cntr 		= 0;

		for (var i = 0; i < firstfew.length; i++){
			markup += '
' + firstfew[i] + '';
		}
		return markup;
	},
	firstfew: 		function(){
		var matches 	= [];
		var search 		= stack_read_more.few.children;
		var cntr 		= 0;

		for (var i = 0; i < search.length; i++){
			if (search[i].tagName === 'P'){
				cntr = cntr + 1;
				matches.push(search[i].innerHTML);
				if (cntr >= 3){
					break;
				}
			}
		}

		return matches;
	}
};

