var stack_insticator 	= {
	build: 	function(){

		// build isnticator widget
		var node 		= document.createElement('div');
		node.setAttributeNode(document.createAttribute('stack-insticator'));
		node.style.paddingTop 		= '20px';
		node.style.paddingBottom 	= '20px';
		node.style.margin 			= '0 auto';

		// insticator container
		var insticator_container 	= document.createElement('div');
		var attr 		= document.createAttribute('id');
		attr.value 		= 'insticator-container';
		insticator_container.setAttributeNode(attr);

		var insticator_ad_1 		= document.createElement('div');
		var attr 		= document.createAttribute('id');
		attr.value 		= 'div-insticator-ad-1';
		insticator_ad_1.setAttributeNode(attr);

		var insticator_embed 		= document.createElement('div');
		var attr 		= document.createAttribute('id');
		attr.value 		= 'insticator-embed';
		insticator_embed.setAttributeNode(attr);

		var insticator_ad_2 		= document.createElement('div');
		var attr 		= document.createAttribute('id');
		attr.value 		= 'div-insticator-ad-2';
		insticator_ad_2.setAttributeNode(attr);

		var insticator_script 		= document.createElement('script');
		var attr 					= document.createAttribute('data-cfasync');
		attr.value 					= 'false';
		insticator_script.setAttributeNode(attr);

		// append everyhting to container
		insticator_container.appendChild(insticator_ad_1);
		insticator_container.appendChild(insticator_embed);
		insticator_container.appendChild(insticator_ad_2);
		insticator_container.appendChild(insticator_script);

		// append everything to node
		node.appendChild(insticator_container);


		// append everything to our placeholder
		document.getElementById('stack_insticator_placeholder').appendChild(node);

		Insticator.ad.loadAd("div-insticator-ad-1");
		Insticator.ad.loadAd("div-insticator-ad-2");
		Insticator.load("em",{id : "1b79f511-38f0-4d43-9966-b750a0909cd1"});

	}
};

