meta.script:
function OGfindMoreImages(){
	
	var i = $('body').find('img');
	var docHead = ('head');
	var base = 'https://www.miltonps.org';
	
	if ($(i).length) {
		$(i).each(function(){
			
			var imgSrc = $(this).attr('src');
			//console.log($(this).parent().parent());
			if (imgSrc) {
				if (imgSrc.indexOf("http") >= 0) {
					$imgSrcComplete = imgSrc;
				} else {
					$imgSrcComplete = base+imgSrc;
				}
				
				$(docHead).append('');
			}
		});
	}
	
}
$(document).ready(function() {
	OGfindMoreImages();
});

div.script:
$(window).load(function() {

	$('.abl-summary-popup-trigger-3294').magnificPopup({
				type:'inline',
				midClick: true,
				gallery:{enabled:true},
				removalDelay: 300,
				mainClass: 'mfp-fade',
				prependTo: $('.bicycle-ui').add('.ccm-page'),
				showCloseBtn: false,
				callbacks: {
					open: function() {
						$('.abl-popup-close-trigger').click(function(evt){
							evt.preventDefault();
							$.magnificPopup.close();
						});
					}
				}
	})

	$('#alert-bar-sums-trigger-3294').magnificPopup({
				type:'inline',
				midClick: true,
				removalDelay: 300,
				mainClass: 'mfp-fade',
				prependTo: $('.bicycle-ui').add('.ccm-page'),
				showCloseBtn: false,
				callbacks: {
					open: function() {
						$('#abl-3294').addClass('abl-popup-open')
						$('#abl-3294').find('.abl-popup-close-trigger').removeClass('abl-popup-close-trigger-hidden')
						$('#abl-3294').find('.abl-popup-close-trigger').click(function(evt){
							evt.preventDefault();
							$.magnificPopup.close();
						});
					},
					afterClose: function() {
						$('#abl-3294').removeClass('abl-popup-open mfp-hide').removeAttr('style')
						$('#abl-3294').find('.abl-popup-close-trigger').addClass('abl-popup-close-trigger-hidden')
					}
				}
	})

});

function googleTranslateElementInit() {
	new google.translate.TranslateElement({
		pageLanguage: 'en', 
		layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
		gaTrack: true, 
		gaId: ''
	}, 
	'google_translate_element');
}

