div.script:
$(document).ready(function() {

	jQuery(function( $ ){

			// Check if alert has been closed
			if( $.cookie('alert-box-6372') === 'closed' ) {
					$('.alert-6372').css({"display":"none"});
			};

			// Grab your button (based on your posted html)
			$('.alert-dismiss-trigger').click(function(evt){

				// Do not perform default action when button is clicked
				evt.preventDefault();

				/* If you just want the cookie for a session don't provide an expires
				 Set the path as root, so the cookie will be valid across the whole site */
				$.cookie('alert-box-6372', 'closed', { path: '/' });

			});
	});

});

// MOVE THE OVERFLOW MENU OUTSIDE THE OUTER-WRAP
$('#overflow-menu-50').clone().insertAfter($('.outer-wrap'));
$('#overflow-menu-50').remove();

$(document).ready(function() {

	$('#dropdown-menu-id-50').dropDownMenu({
		format: 'collapse', 
		speed: 250,
		topLevelCaret: true, // Adds the caret to top level nav items.
		topLevelCaretIconInactive: 'fa-angle-down',
		topLevelCaretIconActive: 'fa-caret-down',
		caretIconInactive: 'fa-angle-down', 
		caretIconActive: 'fa-caret-down',
	});
	
	$('#toggle-menu-50').toggleMenu({
		subMenuCaretInactive: 'fa-angle-down',
		subMenuCaretActive: 'fa-caret-down',
		speed: 250,
	});
	
});


$(document).ready(function() {
	$('.iac-popup-trigger-6376').magnificPopup({
	type:'image'
	});
});

// Responsive Content Choreography - Always puts the image on top of content
var iacBlock = $('#iac-block-6376');

//console.log(iacContentColumnStyle);

$(window).on('resize', function () {


var iacContent = $(iacBlock).find('.iac-content-column');
var iacImage = $(iacBlock).find('.iac-image-column');

	if ($(window).width() < 960) {
		if($(iacContent).hasClass('iac-align-right')) {

			$(iacContent).insertAfter(iacImage); 

		};

	}
	else {

		if($(iacContent).hasClass('iac-align-right')) {

			$(iacContent).insertBefore(iacImage);

		};

	}

}).resize();

