					open: function() {
						$('.abl-popup-close-trigger').click(function(evt){
							evt.preventDefault();
							$.magnificPopup.close();
						});
					}
				}
	})

	$('#alert-bar-sums-trigger-1181').magnificPopup({
				type:'inline',
				midClick: true,
				removalDelay: 300,
				mainClass: 'mfp-fade',
				prependTo: $('.bicycle-ui').add('.ccm-page'),
				showCloseBtn: false,
				callbacks: {
					open: function() {
						$('#abl-1181').addClass('abl-popup-open')
						$('#abl-1181').find('.abl-popup-close-trigger').removeClass('abl-popup-close-trigger-hidden')
						$('#abl-1181').find('.abl-popup-close-trigger').click(function(evt){
							evt.preventDefault();
							$.magnificPopup.close();
						});
					},
					afterClose: function() {
						$('#abl-1181').removeClass('abl-popup-open mfp-hide').removeAttr('style')
						$('#abl-1181').find('.abl-popup-close-trigger').addClass('abl-popup-close-trigger-hidden')
					}
				}
	})

});

