function headerRightSwitch(){
    var logo = $('header').find('#logo-area');
    var auxArea = $('header').find('#aux-area');
    var logoText = $('header').find('#logo-text-items');
    var auxMenu = $('header').find('#aux-menu');
    $(logo).insertAfter(auxArea);
    $(logoText).insertAfter(auxMenu);
}

$(document).ready(function() {
			var imageBreakpointStr = 'iac-sm-';
	var swidth = (window.innerWidth-$(window).width());
	if (imageBreakpointStr.indexOf('-md-') >= 0) {
		var breakpoint = 960 - swidth;
	} else if (imageBreakpointStr.indexOf('-sm-') >= 0) {
		var breakpoint = 768 - swidth;
	}
	
	function imgRightContentChoreo(container,breakpoint) {
		var imgCol = $(container).find('.iac-img');
		var contentCol = $(container).find('.iac-content');
		$(window).on('resize', function () {
			if ($(window).width() < breakpoint) {
				//--	Less than breakpoint
							$(imgCol).insertBefore(contentCol);
			}	else {
				//--	More than breakpoint
							$(imgCol).insertAfter(contentCol);
			}
		}).resize();
	}
	
	imgRightContentChoreo('#iac-1339',breakpoint);
	});

// apply button custom classes...
function customButtonClass520(container){
	var parent = $(container).parent();
	if ($(parent).hasClass('links-btn-primary')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-primary');
	}
	if ($(parent).hasClass('links-btn-success')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-success');
	}
	if ($(parent).hasClass('links-btn-info')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-info');
	}
	if ($(parent).hasClass('links-btn-warning')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-warning');
	}
	if ($(parent).hasClass('links-btn-danger')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-danger');
	}
	if ($(parent).hasClass('links-btn-lg')){
		$(container).find('.btn').addClass('btn-lg');
	}
	if ($(parent).hasClass('links-btn-sm')){
		$(container).find('.btn').addClass('btn-sm');
	}
	if ($(parent).hasClass('links-btn-xs')){
		$(container).find('.btn').addClass('btn-xs');
	}
}
customButtonClass520('#links-list-520');

