div.script:
$(document).ready(function() {
    var triggers = $('#wildapple-object-list-1222').find('.wildapple-object-list-item-popup-trigger');
    if (triggers.length > 0) {
        $(triggers).magnificPopup({
            type:'inline',
            midClick: true ,
            prependTo: $('.bicycleui-page').add('.ccm-page'),
            mainClass: 'mfp-fade',
            removalDelay: 120,
            alignTop: true,
            closeBtnInside: true,
            callbacks: {
                open: function() {
                    var thisPopup = this.currItem.src; //.ev[0].attr('href');
                    $(thisPopup).find('.object-card-tabs-menu').cardTabs({
                            tabs: $(thisPopup).find('.object-card-tabs-items'),
                    });
                },
                close: function() {
                    var thisPopup = this.currItem.src; //$(this).attr('href');
                    $(thisPopup).find('.object-card-tabs-menu').cardTabs({
                        destroy: true
                    });
                }
            }
        });
    }
});

