function showSearch() {
	if(jQuery( ".top_search_form" ).hasClass("remove_search")){
		jQuery( ".top_search_form" ).removeClass("remove_search");
		jQuery( ".top_search_form" ).addClass("display_search");
	}
	else if(jQuery( ".top_search_form" ).hasClass("display_search")){
		jQuery( ".top_search_form" ).removeClass("display_search");
		jQuery( ".top_search_form" ).addClass("remove_search");
	}	
}
