div.script:
$(document).ready(function () {
    $("#b2f23fa7-59aa-4677-9383-25ba8f17a19a").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

jQuery(document).ready(function () {
    jQuery('#8db1c481-f1b2-46c8-9d30-9ad9cf23e443').hide();
    jQuery('#8db1c481-f1b2-46c8-9d30-9ad9cf23e443').listSolrResults({
        currentPagePath: '/content/schools/mvca/en/faqs/coronavirus-faqs',
        label: 'Upcoming Events',
        listFrom: 'solr',
        limit: '5',
        listType: 'event',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/calendar.html',
        viewAllTitle: 'View All',
        uuid: '8db1c481-f1b2-46c8-9d30-9ad9cf23e443',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

