		if(jQuery(url_data_from_link).size() > 1)
		{
			url_data_from_link = url_data_from_link[1];
			url_data_from_link = url_data_from_link.split('&');
			url_day_get_data = "";

			for(var i = 0; i < jQuery(url_data_from_link).size(); i++)
			{
				temp_Get_holder = url_data_from_link[i].split('=');

				if(jQuery(temp_Get_holder).size() > 1)
				{
					if(temp_Get_holder[0] == "fac")
					{
						url_day_get_data += "&fac="+temp_Get_holder[1]+""
					}
					else if(temp_Get_holder[0] == "cat")
