$form = jQuery('.w-form'),
$btnOpen = jQuery( ".w-search-open" );
$btnClose = jQuery('.close_style');
$input = $form.find('.search_feild');
$overlay = jQuery('.w-search-background');
$window = jQuery(window);
searchOverlayInitRadius = 25;
showHideTimer = null;

jQuery( ".w-search-open" ).click(function() {
  	jQuery('.rev_slider_wrapper').css('left','0px');
        jQuery('.jcm_hamburger').css('z-index','100');
	jQuery( ".w-search-background" ).show();

var searchPos = $btnOpen.offset(),
	searchWidth = $btnOpen.width(),
	searchHeight = $btnOpen.height();

	// Preserving scroll position
	searchPos.top -= $window.scrollTop();
	searchPos.left -= $window.scrollLeft();
	var overlayX = searchPos.left+searchWidth/2,
	overlayY = searchPos.top+searchHeight/2,
	//winWidth = $us.canvas.winWidth,
	//winHeight = $us.canvas.winHeight,
	winWidth = jQuery(window).width();
	winHeight = jQuery(window).height();

	// Counting distance to the nearest screen corner
	overlayRadius = Math.sqrt(Math.pow(Math.max(winWidth - overlayX, overlayX), 2) + Math.pow(Math.max(winHeight - overlayY, overlayY), 2)),
	overlayScale = (overlayRadius+15)/searchOverlayInitRadius;

	$overlay.css({
		width: searchOverlayInitRadius*2,
		height: searchOverlayInitRadius*2,
		left: overlayX,
		top: overlayY,
		"margin-left": -searchOverlayInitRadius,
		"margin-top": -searchOverlayInitRadius
	});

	$overlay
		.removeClass('overlay-out')
		.show();

	$form.css({
		opacity: 0,
		display: 'block',
		'-webkit-transition': 'opacity 0.4s 0.3s',
		transition: 'opacity 0.4s 0.3s'
	});

	window.setTimeout(function()
	{
		$overlay
			.addClass('overlay-on')
			.css({
				"-webkit-transform": "scale(" + overlayScale + ")",
				"transform": "scale(" + overlayScale + ")"
			});

		$form.css('opacity', 1);
		clearInterval(showHideTimer);

		showHideTimer = window.setTimeout(function() 
		{
			$input.focus();
			//jQuery(".search_feild").focus();
		}, 700);
	}, 25);


});

//Closing Search overlay button start

jQuery($btnClose).click(function() {
	jQuery('.rev_slider_wrapper').css('left','0px');
	$input.blur();
	$form.css({
		'-webkit-transition': 'opacity 0.4s',
		transition: 'opacity 0.4s'
	});

	window.setTimeout(function(){
		$overlay
			.removeClass('overlay-on')
			.addClass('overlay-out')

			.css({
				'-webkit-transform': 'scale(0.1)',
				'transform': 'scale(0.1)'
			});

		$form.css('opacity', 0);
		clearTimeout(showHideTimer);
		showHideTimer = window.setTimeout(function(){
			$form.css('display', 'none');
			$overlay.css('display', 'none');
			jQuery('.jcm_hamburger').css('z-index','101');
		}, 700);
	}, 25);

});

//Closing Search overlay button end

