/*--- Start Animate down to an element code---*/

function scrollToAnchor(aid){
	//console.log("Start scrollToanchor function");
    var aTag = jQuery("a[name='"+ aid +"']");
    jQuery('html,body').animate({scrollTop: aTag.offset().top},'slow');
	//console.log("End scrollToAnchor function");
}

jQuery(".scrolling_click").click(function() {
	//console.log("Id "+this.id)
	scrollToAnchor(this.id);
});

/*--- End Animate down to an element code---*/

