$(document).ready(function(){
    $("table").find("table[cellpadding]").each(function(i){
        var cellPad = $(this).attr("cellpadding");
        $(this).find("td,th").css({
            "padding" : cellPad
        });
    });
    $("table").find("table[cellspacing]").each(function(i){
        var cellSpace = $(this).attr("cellspacing");
        $(this).css({
            "border-spacing" : cellSpace+"px",
        });
    });

    $("table").find("table[border]").each(function(i){
        var border = $(this).attr("border");
        $(this).css({
            "border" : border+"px solid #ccc",
            "border-collapse" : "separate"
        });
    });
});

$(document).ready(function(){

// press enter key to open dropdown menu
var toggled = false;
$('ul.navbar-nav > li > a').keydown(function(e) {
    if(e.keyCode == 13 && $(this).next().hasClass('sub-menu') !== false ) { // enter key
        toggled = !toggled;
        e.preventDefault();
        $('ul.navbar-nav > li').not($(this).parent()).removeClass('force-show');
        // $(this).parent().attr('class', toggled ? 'force-show':'');
        $(this).parent().addClass('force-show');
        $(this).attr('aria-expanded', toggled ? 'true':'false');
    }
    if(e.keyCode == 40) { // down arrow, focus to first link
        e.preventDefault();
        e.stopPropagation();
        $(this).next().find('a').first().focus();
    }
});
// For sub menu level 2
$('.dropdown-menu > li > a').keydown(function(e){
    if($(this).attr('href') === '#'){
        if(e.keyCode == 13){ // enter key
            e.preventDefault();
            e.stopPropagation();
            $('.dropdown-menu > li').removeClass('force-show');
            $(this).parent().addClass('force-show');
        }
        if(e.keyCode == 40) { // down arrow, focus to first link
            e.preventDefault();
            e.stopPropagation();
            $(this).next().find('a').first().focus();
        }
    }
});

