                            var height = parseInt($('.sticky-hs').outerHeight());
                            if(isNaN(height) || height === null){
                                height = parseInt($('.mid-headaer').outerHeight());;
                            }else{
                                height = height;
                            }
                            if (this.hash !== '') {
                                event.preventDefault();
                                var hash = this.hash;
                                $('html, body').animate({
                                  scrollTop: $(hash).offset().top - height
