header.script:
$(document).ready(function() {
    var he = 300;
    var sheight = window.screen.height;
    var swidth = window.screen.width;
    if(he == 478 || he == 600){
        var h = 300;
    }else{
        var h = 300;
    }
    var w = 1200;
    var h_img = h;
    if(swidth > w){
        h_img = Math.round(h*(swidth/w));
    }
    if(swidth > 768){
        $("#homepage_hs_4").css("max-height", h_img);
        $("#homepage_hs_4 .fullwidthbanner").attr("style", function(i,s){
            var ss = s.split(";");
            $.each(ss,function(q,style){
                if (/^\s?height:\s?[0-9a-zA-Z]+/.test(style)){
                    ss[q] = "height: "+h_img+"px !important";
                }
            });
            return ss.join(";");
        });
        $("#homepage_hs_4 .fullwidthbanner #slide_img_list").attr("style", function(i,s){
            var ss = s.split(";");
            $.each(ss,function(q,style){
                if (/^\s?height:\s?[0-9a-zA-Z]+/.test(style)){
                    ss[q] = "height: "+h_img+"px !important";
                }
            });
            return ss.join(";");
        });
        $("#homepage_hs_4 .fullwidthbanner #slide_img_list .slotholder").css({"height": "100%","display":"block","align-items":"center","justify-content":"center"});
    }
});

