div.script:
var relativeBasePath = '';
var siteSSLurl = 'https://www.fridley.k12.mn.us';
var siteurl = siteSSLurl;
var baseurl = '';

		$j(function(){
			$j(document.body).delegate('a','click',function(){
				var $a,ah;
				//Calculates the link text
				function linkText ($obj) {
					if( $obj.text().length > 0 ){
						return " [" + $obj.text() + "]" ;
					}else if( $obj.find('img').length > 0 ){
						return " [img:" + $obj.find('img').attr('src') + "]";
					}else{
						return "";
					}
				}
				$a = $j(this);
				ah = $a.attr('href');

				// _trackEvent format
				//_gaq.push(['_trackEvent', 'category', 'action', 'optional_label', 'optional_value']);
				// , url, trackingpath + [linked text or img src]

				if(ah && ah.indexOf('page.cfm') < 0 && ah.indexOf('javascript:') < 0 && ah.indexOf('#') != 0){
					if(ah.indexOf('mailto') == 0){
						//Mail to link (mailme links are tracked through mailMe() )
						_gaq.push(['_trackEvent', 'email links', ah, trackingpath + linkText( $a )]);
					}else if(ah.indexOf('http') == 0 && ah.indexOf(document.location.host) < 0){
						//External link
						_gaq.push(['_trackEvent', 'external links', ah, trackingpath + linkText( $a )]);
					}else if(ah.indexOf('file.cfm') >= 0 || !ah.match(/\.cfm$|\.html?$/i)){
						//Assuming these are file downloads
						_gaq.push(['_trackEvent', 'file downloads', ah , trackingpath + linkText( $a )]);
					}
				}
			});
		}); //end load function
