//><!--		//Check for jQuery
		// Note: window.jQuery same as typeof jQuery !== 'undefined'
		if( window.jQuery ){
			jQuery(document).ready(function() {
				jQuery('#aptfinbytap_domain-2-AlpinePhotoTiles_container').addClass('loading');
			});
		}		//Use self invoking function expression
		(function(){
			// Wait for window to load. Them start plugin.
			var alpinePluginLoadingFunction = function(method){
				// Check for jQuery and on() ( jQuery 1.7+ )
				if(window.jQuery){
					if(jQuery.isFunction( jQuery(window).on )){
						jQuery(window).on('load', method);
					// Check for jQuery and bind()
					}else{jQuery(window).bind('load', method);}
				// Check for addEventListener
				}else if( window.addEventListener ){
					window.addEventListener('load', method, false);
				// Check for attachEvent
				}else if ( window.attachEvent ){
					window.attachEvent('on' + 'load', method);
				}
			};
			alpinePluginLoadingFunction( function(){
				if( window.jQuery ){jQuery('#aptfinbytap_domain-2-AlpinePhotoTiles_container').removeClass('loading');
				var alpineLoadPlugin = function(){jQuery('#aptfinbytap_domain-2-hidden-parent').AlpinePhotoTilesPlugin({
						id:'aptfinbytap_domain-2',
						style:'gallery',
						shape:'square',
						perRow:4,
						imageBorder:0,
						imageShadow:0,
						imageCurve:0,
						imageHighlight:0,
						lightbox:0,
						galleryHeight:0,//Keep for Compatibility
						galRatioWidth:800,
						galRatioHeight:600,
						highlight:'#64a2d8',
						pinIt:0,
						siteURL:'https://www.isd381.k12.mn.us',
						callback:''
					});}
				// Load Alpine Plugin
				if( jQuery().AlpinePhotoTilesPlugin ){
					alpineLoadPlugin();
				}else{ // Load Alpine Script and Style
					var css = 'https://www.isd381.k12.mn.us/wp-content/plugins/alpine-photo-tile-for-instagram/css/AlpinePhotoTiles_style.css';
					var link = jQuery(document.createElement('link')).attr({'rel':'stylesheet','href':css,'type':'text/css','media':'screen'});
					jQuery.getScript('https://www.isd381.k12.mn.us/wp-content/plugins/alpine-photo-tile-for-instagram/js/AlpinePhotoTiles_script.js', function(){
						if(document.createStyleSheet){
							document.createStyleSheet(css);
						}else{
							jQuery('head').append(link);
						}alpineLoadPlugin();});// Close getScript
				}}else{ console.log('Alpine plugin failed because jQuery never loaded'); }
			});
		})();//--><!

