var $window = $(window);

function checkWidth() {
    if ($window.width() < 600) {
	var el1 = document.getElementById('sidebar_left');
	var el2 = document.getElementById('sidebar_right');
	if (el1 && el2) {
  	while (el2.firstChild) el1.appendChild(el2.firstChild);
	}

	// Remove el2 if required
	el2.parentNode.removeChild(el2);
    };
}

