        // This is a hack to make the horizontal-nav work correctly. It needs to be resized to full width and then positioned left.
        // This was possible in css before the upgrade to foundation 4.3, so this is a patch.
        // Matthew Sullivan
        $(document).ready(function () {
            if ($(window).width() >= 768) {
                $("#role-nav").find(".content").css({
                    "min-width": $(".horizontal-nav").width()
                });
                $("#parents").css({ "margin-left": -$("#students-title").width() });
                $("#families").css({ "margin-left": -$("#students-title").width() - $("#parents-title").width() });
                $("#community").css({ "margin-left": -$("#students-title").width() - $("#parents-title").width() - $("#families-title").width() });
            }
            if ($(window).width() < 768) {
                $("#role-nav").find(".content").css({
                    "min-width": $(".horizontal-nav").width()
                });
                $("#parents").css({ "margin-left": "0px" });
                $("#families").css({ "margin-left": "0px" });
                $("#community").css({ "margin-left": "0px" });
            }
        });
        $(window).resize(function () {
            if ($(window).width() >= 768) {
                $("#role-nav").find(".content").css({
                    "min-width": $(".horizontal-nav").width()
                });
                $("#parents").css({ "margin-left": -$("#students-title").width() });
                $("#families").css({ "margin-left": -$("#students-title").width() - $("#parents-title").width() });
                $("#community").css({ "margin-left": -$("#students-title").width() - $("#parents-title").width() - $("#families-title").width() });
            }
