function MDELeftNavigation(strTextColor, strHoverColor, strFocusColor, strClassName, 
							strShowHome, strStartLevel, strNumLevels)
{
	this.m_TextColor  = '';
	this.m_HoverColor = '';
	this.m_FocusColor = '';
	this.m_ClassName  = 'MDELeftNavigation';

	this.m_ShowHome   = false;

	this.m_StartLevel = 3;
	this.m_NumLevels  = 10;
	this.m_EndLevel   = 11;

	this.m_NavPath    = g_navNode_Path;

	MDELeftNavigation.prototype.Display = MDELeftNavigation_Display;
	MDELeftNavigation.prototype.DisplayNode = MDELeftNavigation_DisplayNode;

	if (strTextColor != '')
		this.m_TextColor = strTextColor;

	if (strHoverColor != '')
		this.m_HoverColor = strHoverColor;

	if (strFocusColor != '')
		this.m_FocusColor = strFocusColor;

	if (strClassName != '')
		this.m_ClassName = strClassName;

	if (strShowHome == 'true')
		this.m_ShowHome = true;

	if (strStartLevel != '')
	{
		var value = parseInt(strStartLevel);
		if (value != NaN)
			this.m_StartLevel = value;
	}

	if (strNumLevels != '')
	{
		var value = parseInt(strNumLevels);
		if (value != NaN)
			this.m_NumLevels = value;
	}

	this.m_EndLevel = this.m_StartLevel + this.m_NumLevels - 1 ;
}

function MDELeftNavigation_Display (node)
{
	document.write('');
	this.DisplayNode(node);	
	document.write('');
}

function MDELeftNavigation_DisplayNode(node)
{
	var bSelected = false;
	var nodeColor = this.m_TextColor;
	var nodeClass = this.m_ClassName

	var nodeLevel = node.m_level;

	if (nodeLevel > 6)
		nodeLevel = 6;

	if (this.m_NavPath.length > 0 && node.m_level < this.m_NavPath.length)
	{
		if (this.m_NavPath[node.m_level] == node.m_id)
		{
			if (node.m_level > 0 || (node.m_level == 0 && this.m_NavPath.length == 1))
			{
				bSelected = true;
				nodeColor = this.m_FocusColor;
				nodeClass += '-focus';
			}
		}
	}

	if (nodeLevel > 0)
		nodeClass += '-' + nodeLevel;

	if ( (node.m_level == 0 && this.m_ShowHome) || 
     	 (node.m_level >= this.m_StartLevel && node.m_level <= this.m_EndLevel)
	   )
	{
		var ds = new Array();
		var di = 0;


		console.log("nL:", nodeLevel);

		if(nodeLevel > currentNode)// && !startedUL[nodeLevel+1])
		{
			ds[di++] = '
';
			startedUL[nodeLevel+1] = true;
		}

		if(nodeLevel < currentNode)// && startedUL[nodeLevel-1])
		{
			ds[di++] = '';
			startedUL[nodeLevel-1] = false;

		}
		currentNode = nodeLevel;

	//	ds[di++] = (nodeLevel <= 2) ? '<li' : '<li';
		ds[di++] = '
';

		node.m_href = node.m_href.replace("index.html","");
		node.m_href = node.m_href.replace("index.htm","");

		ds[di++] = ''
		ds[di++] = node.m_label;
		ds[di++] = '';

		document.write(ds.join(''));
	}

	if (bSelected || node.m_level == 0)
	{	// expand sub-levels (if any)
		for (var i = 0; i < node.m_subNodes.length; i++)
		{
			this.DisplayNode(node.m_subNodes[i]);
		}
	}
}

