body.script:
if($('.navbar-toggle').css('display') != 'block')
{

	/****
	* Param:
	* 1. tabIndex
	* 2. Image  source
	* 3. height of mega menu
	* 4. Right property to center
	****/
	addThumb('2', '', 'auto', "auto");
	addThumb('5', '', 'auto', "-600px");
	addThumb('6', '', '266px', "-500px");
	addThumb('4', '', 'auto', "-700px");
	addThumb('', '', '', "");

}

function addThumb(tab_index,img,h,r)
{
    if(tab_index=="")
    {
        return;
    }
    $('.menu:nth-of-type('+tab_index+') .megamenu').attr("style", "height:"+h+" !important; right:"+r+" !important; width:1170px !important;");
    $('.menu:nth-of-type('+tab_index+') .megamenu').append('
'+img+'');
}
