bugsnagClientOptions.beforeSend = function(report) {
	var multiplier = Math.pow(10, 2),
		loadedResources = getLoadedResources(),
		requireBaseConfigLoaded = false,
		tags = report.metaData.tags,
		bugsnagErrorUrl = 'https://app.bugsnag.com/eventbrite-1/core-js/errors?sort=last_seen&filters[event.since][0]=1h&filters[app.release_stage][0][type]=eq&filters[error.status][0][type]=eq&filters[error.status][0][value]=open&filters[app.context][0][type]=eq' +
			'&filters[app.release_stage][0][value]=' + report.app.releaseStage +
			'&filters[app.context][0][value]=' + report.context,
		allScriptTags;
	function round(number) {
		return Math.round(number * multiplier) / multiplier;
	}
	function getLoadedResources() {
		try {
			return performance
				.getEntriesByType('resource')
				.filter(function(resource) {return resource.initiatorType === 'script' || resource.initiatorType === 'link';})
				.map(function(resource) {return '(' + round(resource.duration) + 'ms) ' + resource.name;});
		} catch(ex) {
			return [];
		}
	}
	report.updateMetaData('loadedResources', loadedResources);
	// Do some extra checking for the listing page which is a cause of a lot
	// of script loading issues
	if (report.context === 'listing') {
		// check to see if require_base_config.js was loaded
		for (var i = 0; i < loadedResources.length; i++) {
			if (loadedResources[i].indexOf('require_base_config.js') > -1) {
				requireBaseConfigLoaded = true;
				break;
			}
		}
		// if not, find the script tag with the src and make a fetch call
		// to see if there's a problem with the URL, it was a blip, etc.
		// this should help debug the problem better.
		if (!requireBaseConfigLoaded && window.fetch) {
			allScriptTags = document.querySelectorAll('script');
			for (var i = 0; i < allScriptTags.length; i++) {
				if (allScriptTags[i].src && allScriptTags[i].src.indexOf('require_base_config.js') > -1) {
					// returning a promise makes beforeSend act asynchronously so it'll
					// wait until the promise resolves/rejects.
					// if the fetch was successful, then we return the status code. this would
					// mean that the initial load for require_base_config failed, but a subsequent
					// one passed. if the fetch was unsuccessful then we'll get the error message
					// to see what's going on.
					return fetch(allScriptTags[i].src)
						.then(
							function(resp) { tags['require_base_config'] = resp.status; },
							function(err) { tags['require_base_config'] = err.message; }
						)
						.then(function() {tags['require_base_config_url'] = allScriptTags[i]; });
				}
			}
		}
	}
	report.updateMetaData('loadedResources', loadedResources);
};
