                "alt": "Saint Paul Public Schools" // ALTERNATE LOGO ALT TEXT
            },
            "toggle": function(on) {
            	if(on) {
                    $.each(socailIcons, function(index, icon) {
                        if(icon.highContrastImgSrc.indexOf("Faces/default-man.jpg") == -1) {
                            $(".social-icon-image img").eq(index).attr("src", icon.highContrastImgSrc); 
                        } else {
                            $(".social-icon-image img").eq(index).attr("src", icon.defaultHighContrastImgSrc);
                        }
                    });
                } else {
              		$.each(socailIcons, function(index, icon) {
                        if(icon.imgSrc.indexOf("Faces/default-man.jpg") == -1) {
                            $(".social-icon-image img").eq(index).attr("src", icon.imgSrc); 
                        } else {
                            $(".social-icon-image img").eq(index).attr("src", icon.defaultImgSrc);
                        }
                    });
                }      
            } // CUSTOM CALLBACK WHEN CONTRAST IS TOGGLED (ALSO FIRES ON DOC READY) - on PARAMETER IS EITHER true OR false (boolean)
        });

