	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			if(!$(".gb-header-mystart #sw-mystart-mypasskey").length) {
				$(".gb-header-mystart .sw-mystart-button.schools").after($("#sw-mystart-mypasskey"));
			}
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			if(!$("#gb-channel-list #sw-mystart-mypasskey").length) {
				$("#gb-channel-list .gb-channel-search-box").after($("#sw-mystart-mypasskey"));
			}
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			if(!$("#gb-channel-list #sw-mystart-mypasskey").length) {
				$("#gb-channel-list .gb-channel-search-box").after($("#sw-mystart-mypasskey"));
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			if(!$("#gb-channel-list #sw-mystart-mypasskey").length) {
				$("#gb-channel-list .gb-channel-search-box").after($("#sw-mystart-mypasskey"));
			}
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			if(!$("#gb-channel-list #sw-mystart-mypasskey").length) {
				$("#gb-channel-list .gb-channel-search-box").after($("#sw-mystart-mypasskey"));
			}
		}
	}

	function photoGallery() {
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length && "false" == "true"){
            // MULTIMEDIA ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                    "imageWidth"              : 1500,
                    "imageHeight"             : 770,
                    "titles"                  : "yes",
                    "captions"                : "yes",
                    "linkedElement"           : ["image"],
                    "mainControls"            : "yes",
                    "hoverPause"              : "yes",
                    "autoRotate"              : "yes",
                    "fullScreenRotator"       : "no",
                    "fullScreenBreakpoints"   : ["960"],
                    "transitionType"          : "fade",
                    "transitionSpeed"         : 3,
                    "transitionDelay"         : 8,
                    "allLoaded"               : function() {
                        $(".fullscreen-photo-true").hover(function(){
                            $(".fullscreen-photo-true .rs-gallery-control").animate({ opacity: 1 }, 500, function() { });
                        }, function() {
                            $(".fullscreen-photo-true .rs-gallery-control").animate({ opacity: 0 }, 500, function() { });
                        });
                    }
            });
		} else if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length) {
            // MULTIMEDIA ROTATOR
            $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                    "imageWidth"              : 605,
                    "imageHeight"             : 390,
                    "titles"                  : "yes",
                    "captions"                : "yes",
                    "mainControls"            : "yes",
                    "linkedElement"           : ["image"],
                    "hoverPause"              : "yes",
                    "autoRotate"              : "yes",
                    "transitionType"          : "fade",
                    "transitionSpeed"         : 3,
                    "transitionDelay"         : 8
            });
		}
	}

	function multimediaContentRotators() {
        // MULTIMEDIA ROTATOR
        $("div.ui-widget.app.cs-rs-multimedia-rotator").not($("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator")).not($("div.ui-widget.app.cs-rs-multimedia-rotator.detail")).each(function() {
        // GET THE DATA ARRAY
        var moduleID = $(this).parent().attr("id").replace("module-content-", "");
        var dataArray = eval("appData" + moduleID);
        // ADD CUSTOM CLASS
        $(this).addClass("cs-default-gallery");
            // RUN THE PLUGIN
            $(this).csRsMultimediaRotator({
                    "imageWidth"              : 605,
                    "imageHeight"             : 390,
                    "appHeader"               : "yes",
                    "titles"                  : "yes",
                    "captions"                : "yes",
                    "galleryOverlay"	      : "no",
                    "linkedElement"		      : ["image"],
                    "playPause"               : "no",
                    "mainControls"            : "yes",
                    "transitionSpeed"         : 3,
                    "transitionDelay"         : 8
			});
	   });
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
Coronavirus Disease (COVID-19)");
		}
    }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");
		$("#sw-mystart-search").appendTo($(".gb-channel-search-box"));
        $(document).on("click keydown", ".gb-channel-search", function(e) { 
            if(A11yClick(e)) {
            	e.preventDefault();
                $(".gb-channel-search-box").slideToggle(500, function(){
                	$("#sw-search-input").focus();
                });
            }
        });

		//Added by Marie - Case 00173228
		$(".sw-mystart-button.signin a span").text("Staff Login");
        $("#sw-mystart-right .sw-mystart-button, #sw-mystart-right #sw-mystart-account").remove();

		// ********** HEADER ********** //
		$(".rs-menu-option.schools").load("/cms/lib/MN01910242/Centricity/Template/28/html-files/school-list.html", function(){
        	$(".rs-menu-option .rs-menu-option-sublist > li a, .rs-menu-option-sublist-back, .rs-menu-option-sublist-menu-close").attr("tabindex", "-1");

        	$(document).on("click keydown", ".sw-mystart-button.schools", function(e) { 
                if(A11yClick(e)) {
                    e.preventDefault();
                    elemPos = "right";
                    animation = {right: "0px"};
                    $("#rs-menu-bg").fadeIn(function() {
                        $("#rs-menu-bg").removeClass("closed").addClass("open").removeAttr("style");
                        $("#rs-menu").removeClass("closed").addClass("open");
                        $("#rs-menu").animate(animation, 500, function(){
                            $("#rs-menu-search-input").focus();
                        });
                    });
                }
            });

            $(document).on("click keydown", ".rs-menu-option-bullet", function(e) { 
                if(A11yClick(e)) {
                    var subMenuParent = $(this).parent().parent();
                    elemPos = "right";
                    animation = {right: "0px"};
                    $(".rs-menu-option-sublist", subMenuParent).animate(animation, 500, function(){
                        $(".rs-menu-option-sublist", subMenuParent).removeClass("closed").addClass("open");
                        $(".rs-menu-option-sublist > li a", subMenuParent).attr("tabindex", "0");
                        $(".rs-menu-option-sublist-back", subMenuParent).attr("tabindex", "0").focus();
                        $(".rs-menu-option-sublist-menu-close", subMenuParent).attr("tabindex", "0");
                    });
                } else if($(this).parents(".rs-menu-option.schools").index() == $(".rs-menu-option.schools").length-2) {
                	switch(e.keyCode) {
                        // CONSUME TAB KEY
                        case KeyCodes.tab:
                            e.preventDefault();
                            $("#rs-menu-close-btn").focus();
                        break;
                	}
                }
            });

            $(document).on("click keydown", ".rs-menu-option-sublist-back", function(e) { 
                if(A11yClick(e)) {
                    elemPos = "right";
                    animation = {right: "-320px"};
                    $(".rs-menu-option-sublist.open").animate(animation, 500, function(){
                        $(".rs-menu-option-sublist.open").removeClass("open").addClass("closed");
                        $("#rs-menu-search-form").focus();
        				$(".rs-menu-option .rs-menu-option-sublist > li a, .rs-menu-option-sublist-back, .rs-menu-option-sublist-menu-close").attr("tabindex", "-1");
                    });
                }
            });

            $(document).on("click keydown", ".rs-menu-option-sublist-menu-close", function(e) { 
                if(A11yClick(e)) {
                    elemPos = "right";
                    animation = {right: "-320px"};
                    $(".rs-menu-option-sublist.open").animate(animation, 500, function(){
                        $(".rs-menu-option-sublist.open").removeClass("open").addClass("closed");
                        $("#rs-menu").animate(animation, 500, function(){
                            $("#rs-menu").removeClass("open").addClass("closed");                             
                            $("#rs-menu-bg").fadeOut(function() {
                                $("#rs-menu-bg").removeClass("open").addClass("closed").removeAttr("style");
        						$(".rs-menu-option .rs-menu-option-sublist > li a, .rs-menu-option-sublist-back, .rs-menu-option-sublist-menu-close").attr("tabindex", "-1");
                            });
                        });
                    });
                }
            });

            $(document).on("keydown", ".rs-menu-option.schools .rs-menu-option-sublist li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME TAB KEY
                    case KeyCodes.tab:
                    	e.preventDefault();
                        if($(this).parent().is(":last-child")) {
                            $(this).parents(".rs-menu-option-sublist").find(".rs-menu-option-sublist-back").focus();
                        } else {
                        	$(this).parent().next("li").find("a").focus();
                        }
                    break;
                }
            });

		});

		// AJAX ALERT
		if($(".sp").length) {
		    $.ajax({   
		        url : "/spps",
		        type : "GET",
		        success : function(data) {
                    $("#gb-emergency-outer").append($(data).find("#gb-emergency"));
                    if($("#gb-emergency div.ui-widget.app div.ui-widget-detail li").length != 0) {
                        $("#gb-emergency-outer").toggle();
                    }
		        }
		  });
		} else {
			if($("#gb-emergency div.ui-widget.app div.ui-widget-detail li").length != 0) {
				$("#gb-emergency-outer").toggle();
			}
		}
		if(5 == 0) {	
			$(".gb-header-icons").hide();
		} else {
			for(i=5;i >= 5; i--) {
				$(".gb-header-icons a").eq(i).hide();
			}
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
