					$(this).parent().remove();
				}
			});

            // NAVIGATION APP HOVER
            $(document).on("mouseenter focusin", "div.ui-widget.app.navigation div.ui-widget-detail > ul > li", function() {
            	$(this).addClass("hover");
            }).on("mouseleave focusout", "div.ui-widget.app.navigation div.ui-widget-detail > ul > li", function() {
            	$(this).removeClass("hover");
            });

            // UPCOMING EVENTS - MOVE EVENT TIME TO DISPLAY AFTER THE TITLE
            $(".upcomingevents .ui-article-description").each(function() {
            	var thisEvent = this;
                if($(".sw-calendar-block-time",thisEvent).length) {
                	$(thisEvent).find(".sw-calendar-block-time").appendTo(thisEvent);
                }
            });
        },

