      viewToUse = $("#hid-" + MIID + "-SidebarListView").val();
    };
  GetContent('https://www.pcsd.ms//cms/UserControls/ModuleView/ModuleViewRendererWrapper.aspx?DomainID=473&PageID=1252&ModuleInstanceID=' + MIID + '&PageModuleInstanceID=' + PMI + '&RenderLoc=0&FromRenderLoc=0&GroupYear=' + groupYear + '&GroupMonth=' + groupMonth + '&GroupByField=' + groupBy + '&EnableQuirksMode=0&ViewID=' + viewToUse + '&Tag=' + tag, container, 2, 'chkSidebar();');
}
